credentialServices.airkit_schema_json:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "credentialSubject": {
      "type": "object",
      "properties": {
        "yearsOfExperience": {
          "type": "number",
          "title": "Years of Experience"
        },
        "expertiseLevel": {
          "type": "string",
          "title": "Expertise Level",
          "enum": ["Beginner", "Intermediate", "Advanced", "Expert"]
        },
        "lastProjectDate": {
          "type": "string",
          "format": "date",
          "title": "Last Project Date"
        },
        "projectsCompleted": {
          "type": "number",
          "title": "Projects Completed"
        },
        "specialization": {
          "type": "string",
          "title": "Specialization Area"
        }
      },
      "required": ["yearsOfExperience", "expertiseLevel"]
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "credentialSubject": {
      "type": "object",
      "properties": {
        "walletAgeYears": {
          "type": "number",
          "title": "Wallet Age (Years)"
        },
        "totalTransactions": {
          "type": "number",
          "title": "Total Transactions"
        },
        "defiProtocolsUsed": {
          "type": "number",
          "title": "DeFi Protocols Used"
        },
        "lastActiveDate": {
          "type": "string",
          "format": "date",
          "title": "Last Active Date"
        },
        "trustScore": {
          "type": "number",
          "title": "Trust Score",
          "minimum": 0,
          "maximum": 100
        },
        "walletType": {
          "type": "string",
          "title": "Wallet Type",
          "enum": ["EOA", "Smart Contract", "Multisig"]
        }
      },
      "required": ["walletAgeYears", "trustScore"]
    }
  }
}

